function Object_Map(obj, map, parent = false)
{ 
 if(!map) var map = [];
 
 var keys = Object.keys(obj);
 
 for(var key of keys)
 {
  var item         = {};
  item["key"]      = key;
  item["source"]   = obj[key];
  item["count"]    = 1;
  item["parent"]   = parent;
  item["children"] = [];
  
  if(parent) parent["children"].push(item);
  
  
  Object_Map(obj[key], map, item);
  
  var depth   = 0;
  var pointer = item["parent"];
  
  while(pointer)
  {
   pointer["count"]++;
   pointer = pointer["parent"];
   
   depth ++;
  }
  
  if(!map[depth]) map[depth] = [];
  
  map[depth].push(item);
 }
  
 return map;
}



