 for(var tag of tags)
 {  
  // FIND TAG AND EMPTY IT
  var search = delimiter_a + tag + delimiter_b;
  var i      = string.indexOf(search); 
  string     = string.replace(search, " ".repeat(search.length));
  
  // PRE-TAG CONTENT
  var text = string.substring(c, i);
  if(text.length > 0)
  {
   var block        = {};
   block["type"]    = "text";
   block["content"] = text;
   blocks.push(block);
  }

  // TAG CONTENT
  var block        = {};
  block["type"]    = "tag";
  block["content"] = tag;
  blocks.push(block);
  
  // MOVE ON
  c = i + search.length;
 }

 // REMAINDER
 var text = string.substring(c, string.length);
 if(text.length > 0)
 { 
  var block        = {};
  block["type"]    = "text";
  block["content"] = text;
  blocks.push(block);
 }

