function Video_Line_Current(lines, time)
{
 for(var line of lines)
 {
  if(time > line["start"] && time < line["end"])
  {
   return line;
   break;
  }	  
 }
 
 return false;
}




function Video_Line_Previous(lines, time)
{
 var previous = false;

 for(var line of lines)
 {
  if(line["end"] < time && (!previous || previous["end"] < line["end"]))
  {
   previous = line; 
  }	  
 }
 
 return previous;
}









