function Vocabulary_Term_Purge(term)
{
 var splitter = false;
 for(var char of ["_", "-"])
 {
  if(term.includes(char))
  {
   splitter = char;
   break;
  }
 }
 
 if(!splitter) return term;
 
 term = term.split(splitter);
 return term[0];
}



